vec4 v;
//frag Color,Coord
void mainImage(out vec4 a,in vec2 b){

	vec2 u=(b.xy/iResolution.y);

	vec3 p=vec3(u,2.+length(u)-1.*sin(u.x)),
	o=p.xzy,
	c,
	q=vec3(-2.3,1.19,-3.4)+.601*vec3(.3,.98,.034);

	float T=iTime*.01,
	S=sin(T),
	m=v.z+=iMouse.x*.01,
	C=cos(T-m*S),
	x=o.x,
	z=o.z;

	o.xz=vec2(S*x-C*z,C*x+S*z);

	z=0.;
	for(int i=0;i<33;++i){
		p=q+z*o;
		for(int i=0;i<33;++i){
			x=dot(p,p);
			v+=vec4(p=abs(p)/x,x);
			p-=vec3(.5,.4,1.5);
		};
		v/=32.;
		z+=max(
			.001*m*S,
			min(.01/m,x=1.-abs(1.-x))
		)*(
			.3+3.*z
		);
		c+=(
			.5+.5*sin(v.xyz*30.)
		)/(
			1.+x*x*1E3
		);
	};

	a=vec4(
		pow(
			c/5.,
			vec3(.7)
		),
		1.
	);
}
