float height (vec3 p)
{
    return texture(iChannel1, p.xy/20.).x*.25 + p.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord- .5 * iResolution.xy)/iResolution.y;

    vec3 ro = vec3(iTime/40., iTime*1.5, .2);
    vec3 rd = normalize(vec3(uv.x, 1.5, uv.y));

    float a = sin(iTime/15.)/10. - .1;
    rd.yz *= mat2(cos(a),-sin(a),sin(a),cos(a));


    vec3 p = vec3(ro.xy - ro.z/(rd.z/rd.xy), 0.);
    float d = length(p - ro);

    for (int i = 0; i <= 120; i++)
    {
        p = ro + rd*d;
        d += height(p);
    }

    fragColor = vec4(0);
    for (float o = 0.; o <= 5.; o++)
    {
        fragColor += texture(iChannel0, p.xy + vec2(0, o/100.))*.2;
    }

    fragColor *= (1.-3.*height(p));

    if (p.z > .0) fragColor *= 0.;
}
