// SDF of a shape made of a set line and arc segments
float sdShape( in vec2 p, int kType[4], float kPath[10] )
{
    vec2 vb = vec2(kPath[0],kPath[1]);

    float d = dot2(p-vb);
    int off = 0;
    float s = 1.0;
    for( int i=0; i<kType.length(); i++ )
    {
        vec2 va = vb;
        vec2 ds;

        if( kType[i]==0) // line (x,y)
        {
            vb = vec2(kPath[off+2],kPath[off+3]);
            ds = sdSqLine( p, va, vb );
            off += 2;
        }
        else if( kType[i]==1) // arc (x,y,r)
        {
            vb = vec2(kPath[off+3],kPath[off+4]);
            ds = sdSqArc(p, va, vb, kPath[off+2], d );
        	off += 3;

        }

        // in/out test
        bvec3 cond = bvec3( p.y>=va.y, p.y<vb.y, ds.y>0.0 );
        if( all(cond) || all(not(cond)) ) s*=-1.0;  

        d = min( d, ds.x );
    }
	return s*sqrt(d);
}

float sdA( in vec2 p, float t )
{
    float height = 1. + sin(t*PI*2.)*0.2;
    float width = .8 + cos(t*PI*2.)*0.1;
    float strokeWidth = 0.25 + cos(t*PI*2.)*0.1;

   	int kType[] = int[](0,0,0,0);
	float kPath[] = float[]( -strokeWidth*0.5, height*0.5,
                             -strokeWidth*0.5,-height*0.5,
                              strokeWidth*0.5,-height*0.5,
                              strokeWidth*0.5, height*0.5,
                             -strokeWidth*0.5, height*0.5 );
    return sdShape(p,kType,kPath );
}

vec3 generateBorder(in vec3 col, in float border)
{
    col = mix( col, vec3(0.0), 1.0-smoothstep(0.0,0.015,border) );
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    float t = mod(iTime * 0.25, 1.);


    vec3 col = vec3(1.,1.,1.);
    for (int i=0; i<25; i++) {
        //float d = sdA(p, t+float(i)*0.08);
        float d = sdA(p+vec2(float(15-i)*0.004), t+float(i)*0.005);
    	col = float(i)*0.003+generateBorder(col, abs(d));
    }
    col = 1. - col;

    //col = vec3(sign(d));

    // output
	fragColor = vec4(col, 1.0);
}
