void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float colCoef = mod(iTime, 10.0)/10.0; // Коефіцієнт кольору, який міняється з часом ( Коефіцієнт від 0.0 до 1.0 )
    if(colCoef>0.5){
        colCoef = 1.0 - colCoef; // Якщо коефіцієнт більше 0.5, перевертаємо його, роблячи зациклення ( Стає 0.0 до 0.5
    }
    colCoef += 0.5; // Збільшуємо коефіцієнт на 0.5 ( Тепер він від 0.5 до 1.0)
    vec2 uv = (fragCoord/iResolution.xy)*vec2(4,4)-vec2(2,2); // Перетворюємо координати на діапазон від -2 до 2
    float formulaCol = (uv.x*exp(-(pow(uv.x, 2.0)+pow(uv.y, 2.0)))+2.0)/4.0; // Виконуємо f(x, y) = xe−(x2 + y2) 
    float col = -formulaCol+colCoef; // Виконуємо накладання коефеціенту на колір
    fragColor = vec4(0, 0, col, 1.0); // Виводимо колір на екран з прозорістю 1 ( НЕ прозорий )
}
