void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    float pi = 3.14159265359;
    float p = 1.+sin(iTime)*.05;
    u *= p;
    vec2 v = u;
    vec2 m = vec2(.25,.6);  if(iMouse.z>0.)m=iMouse.xy/iResolution.xy;
    float e = 16.*m.x-8.;
    for(float i = 0.; i < 8.*m.y; ++i)
    {
        u = sin(atan(u.y,u.x)*e+vec2(0.,pi*.5))*pow(length(u),e*.6);
        u+= v/p;
    }
    fragColor = vec4(exp(dot(u,u)*-.1));
}
