// This is a DFT with some distortion and colouring

#define tau 6.28

float af(float i){return sin(i*(400.+sin(iTime)));}
//float f(float i){return fract(i*(400.+sin(iTime)));}
vec4 f(vec2 i){return texture(iChannel0,vec2(i));}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float iters = 47.;
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y*iters;

    vec3 col = vec3(0);

    vec2 p = vec2(0);

    for(float x = 0.; x < iters; x++){
        for(float y = 0.; y < iters; y++){
            float coord = tau*(uv.x*x+uv.y*y)/iters;
            float fn = f(vec2(x,y)/iters).y;
            p+=vec2(cos(coord),sin(coord+1.))*fn;
        }
    }


    // coloring
    col += 0.5+sin(vec3(4,1,1)+5.*length(p)/iters)+ length(p)/iters;
    col *= length(p)/iters;

    fragColor = vec4(col,1.0);
}
