void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = iTime * .17;
    float zoom = 50.0 + sin(10.0 * iTime);
    float clarity = abs(sin(iTime));
    vec4 color_A = vec4(abs(sin(iTime)), 0.60, 0.90, 1);
    vec4 color_B = vec4(0, 0.50, abs(sin(iTime)), 1);

    vec2 uv = fragCoord / iResolution.xy;

    float rad = radians(angle);
    vec2 direction = vec2( abs(0.25*cos(angle) * uv.x), abs(0.25*sin(angle) * uv.y));

    float position = direction.x + direction.y;
    float wave = sin(position * zoom) * clarity;


    fragColor = mix(color_A, color_B, clamp(wave, -1.0, 1.0));
}
