float GatherSoundInfo(int SampleWished)// 0 - 512
    {
        return texelFetch(iChannel0, ivec2(float(SampleWished), 0), 0).r;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-iResolution.xy/2.)/iResolution.y;
	uv *=9.;
    uv += vec2(0.5,-.5);

    vec3 col;
 	vec2 mineUV = fract(iTime) >0.5 ? floor(uv) : uv -vec2(0.5,-.3);
    float redF = GatherSoundInfo(512/32 * int(abs(mineUV.x)+abs(mineUV.y)));
    vec3 red = vec3(redF,0.1,0.1);
    vec3 black = 0.5-vec3(GatherSoundInfo(512/16 * int(abs(mineUV.x)+abs(mineUV.y))));

    col = abs(mineUV.x)+abs(mineUV.y)>4. ? black : red;
	col = mineUV.y > 1. ? black : col;
    col = mineUV.x == 0. && mineUV.y == 1. ? black : col;
    col = abs(mineUV.x) == 2. && mineUV.y == 2. ? red : col;

    fragColor = vec4(col,1.0);
}
