void mainImage( out vec4 fragColor, in vec2 pos )
{
   vec4 potential = SAMPLE(iChannel1, pos, size);

   fragColor = vec4(0.01*abs(potential.xxx) + 1.*vec3(0.2,0.5,1.)*abs(potential.y) + 1.*sin(0.5*potential.z*vec3(1.*cos(0.1*potential.x),2,3.*cos(0.1*potential.x))),1.0);
}

//voronoi particle tracking 

void Check(inout vec4 U, vec2 pos, vec2 dx)
{
    vec4 Unb = SAMPLE(iChannel0, pos+dx, size);
    vec2 rpos1 = mod(pos-Unb.xy+size*0.5,size) - size*0.5;
    vec2 rpos2 = mod(pos-U.xy+size*0.5,size) - size*0.5;
    //check if the stored neighbouring particle is closer to this position 
    if(length(rpos1) < length(rpos2))
    {
        U = Unb; //copy the particle info
    }
}

vec4 B(vec2 pos)
{
   return SAMPLE(iChannel1, pos, size);
}

void mainImage( out vec4 U, in vec2 pos )
{
    U = SAMPLE(iChannel0, pos, size);

    //check neighbours 
    Check(U, pos, vec2(-1,0));
    Check(U, pos, vec2(1,0));
    Check(U, pos, vec2(0,-1));
    Check(U, pos, vec2(0,1));
    Check(U, pos, vec2(-1,-1));
    Check(U, pos, vec2(1,1));
    Check(U, pos, vec2(1,-1));
    Check(U, pos, vec2(-1,1));

    vec2 ppos = U.xy;

    //potential gradient
    vec4 Fdx = B(ppos+vec2(1,0)) - B(ppos+vec2(-1,0));
    vec4 Fdy = B(ppos+vec2(0,1)) - B(ppos+vec2(0,-1));

    vec2 repulsion = 0.2*vec2(Fdx.w, Fdy.w);
    vec2 gravity = 0.01*vec2(Fdx.x, Fdy.x);

    //mouse interaction
    if(iMouse.z>0.)
    {
       if(mod(pos.x,4.)<1. && mod(pos.y,4.)<1. && length(iMouse.xy-pos) < 30. )
       {
           //init position and velocity
           U = vec4(pos,0.,0.);
       }
    }

    //update the particle
    U.zw = U.zw*0.984 + dt*(gravity - repulsion);
    U.xy = mod(U.xy + (U.zw-0.4*vec2(Fdx.y, Fdy.y))*dt,size); //limit the position to the texture and integrate position

    //initial condition
    if(iFrame < 1)
    {
       if(mod(pos.x,6.)<1. && mod(pos.y,6.)<1. && abs(pos-size*0.5).x < vec2(size*0.3).x && abs(pos-size*0.5).y < vec2(size*0.3).y)
       {
           //init position and velocity
           U = vec4(pos,0.,0.);
       }
    }
}

#define size iResolution.xy
#define SAMPLE(a, p, s) texture((a), (p)/s)

float gauss(vec2 x, float r)
{
    return exp(-pow(length(x)/r,2.));
}

#define PI 3.14159265
#define dt 0.65

const float radius = 1.0;

//solve the poisson equation for gravity and repulsion

vec4 B(vec2 pos)
{
   return SAMPLE(iChannel1, pos, size);
}

vec4 pdensity(vec2 pos)
{
   vec4 particle_param = SAMPLE(iChannel0, pos, size);
   return vec4(1.,1.,1.,1.)*gauss(pos - particle_param.xy, radius);
}

const vec4 damp = vec4(0.0004,0.3,1.,0.04);
const vec4 ampl = vec4(0.1,1.,1.,0.1);

void mainImage( out vec4 U, in vec2 pos )
{
    vec4 density = pdensity(pos);
    U.z = B(pos).z;
    U.xyw = (1.-damp.xyw)*0.25*(B(pos+vec2(0,1))+B(pos+vec2(1,0))+B(pos-vec2(0,1))+B(pos-vec2(1,0))).xyw;
    U += density*ampl;
    U.z*=0.9;
}
