void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //move value range so 0 is center
	uv -= .5;

    //hourglass coords
    vec2 weirduv = vec2(atan(uv.r / uv.g),atan((uv.r / uv.g) * uv.r * 3.0 + uv.g));

    //animate coords
    weirduv.r += iTime/20.0;
    weirduv.g += iTime/5.0;

    // Output to screen
    fragColor = vec4(texture(iChannel0,weirduv));
}
