#define red vec3(1., .0, .0)
#define white vec3(1.)
#define green vec3(.0, 1., .0)
#define blue vec3(.0, .0, 1.)
#define pi 3.1415926

float cosineCurve(float t)
{
    return (1. - cos(t * pi))/2.;
}

float circle(vec2 uv, vec2 circleCenter, float r, float blur)
{
    float d = length(uv - circleCenter);
    return smoothstep(r, r-blur, d);
}

vec2 rotate(vec2 v, float a) 
{
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

float getBackGround(vec2 uv)
{
    return length(uv);
}

float smile(vec2 uv, vec2 center, float size) 
{
    uv -= center;
    uv /= size;

	float mask = circle(uv, vec2(.0), .3, .009);
    mask -= circle(uv, vec2(-.1, .09), .0625, .01);
    mask -= circle(uv, vec2(.1, .09), .0625, .01);

    float mouth = circle(uv, vec2(.0), .2, .01);
    mouth -= circle(uv, vec2(.0, .05), .2, .01);
    mask -= mouth;

    return mask;
}

vec2 wave(vec2 uv, float a, float b) 
{
    float X = uv.x*a+iTime;
    float Y = uv.y*a+iTime;
    uv.y += cos(X+Y)*b*cos(Y);
    uv.x += sin(X-Y)*b*sin(Y);
	return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float mask = .0;
    mask = smile(uv, vec2(-.55, .15), 1.);
    mask += smile(uv, vec2(.55, .15), 1.);
    vec3 col = vec3(1., 0., 0.) * mask;

    uv = wave(uv, 25., .02);
    if (mask == 0. && uv.y < -.4)
    {
        col = blue;
    }


	fragColor = vec4(col, 1.0);
}
