float brightness( vec3 col )
{
    float outp = (col.r + col.g + col.b)/3.0;
    return outp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.xy/iResolution.xy);
    vec2 YOUVEE = vec2(fragCoord.xy/iResolution.xy);
    uv *= 4.0;
    uv.x *= uv.y*2.0;
    vec3 texColor = texture(iChannel0, vec2(uv.x/4.0 - iTime/7.0, uv.y)).rgb;
    vec3 supcolor = texture(iChannel1, YOUVEE).rgb;
    float average = brightness(texColor);
    average -= 0.5;
    average = abs(average);
    average *= 2.0;
    average = 0.9 - average;
    average = step(0.8, average);
    texColor.r = average;
    texColor.g = average;
    texColor.b = average;
    texColor.r += (supcolor.r * 2.0 * abs(sin(iTime)));
    fragColor = vec4(texColor, 1.0);
}
