void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2( fragCoord.xy / iResolution.xy );

    vec3 bgCol= vec3(0.2,0.2,0.2);
    vec3 horLines= vec3(0.8,0,0);
    vec3 vertLines= vec3(0,0,0.8);
    vec3 axesColor= vec3(0,0.8,0);
    const float drawLineComparer= 0.1;
    const float lineWidth=0.002;

    vec3 objCol=bgCol;

    //Draw the divisor lines
    for(float i=0.0;i<1.0;i+=drawLineComparer)
    {
     	if(abs(uv.x-i)<lineWidth) objCol=vertLines;
        if(abs(uv.y-i)<lineWidth) objCol=horLines;
    }

   	// Draw the axis
	if( abs(uv.x)<0.005 ) objCol = axesColor;
	if( abs(uv.y)<0.01 ) objCol = axesColor;


    fragColor=vec4 (objCol, 1.0);

}
