mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(vec2(c, -s),vec2( s, c));
}
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec2 mouse=(2.0*iMouse.xy-iResolution.xy)/iResolution.y;



   vec3 col=vec3(1.0,0.5,0.1);


   float btn=sdBox(p,vec2(0.5,0.5));
   float btnRegion=sdBox(mouse,vec2(0.5,0.5));

   float btn2=sdBox(p+vec2(1.2,0.0),vec2(0.5,0.5));
   float btnRegion2=sdBox(mouse+vec2(1.2,0.0),vec2(0.5,0.5));

   float painel=sdBox(p,vec2(2.0));
   float painelRegion=sdBox(mouse+vec2(1.2,0.0),vec2(0.5,0.5));



   if(painel< 0.0) col = vec3(1.0);

     if (btnRegion<=0.0 && iMouse.z<=0.0){

        if(btn< 0.0) col = vec3(0.0,0.6,0.2);



    }else{

         if(btn< 0.0) col = vec3(1,0.0,0.0);

    }

    if (btnRegion2<=0.0 && iMouse.z<=0.0){

        if(btn2< 0.0) col = vec3(0.0,0.6,0.2);




    }else{

         if(btn2< 0.0) col = vec3(1,0.0,0.0);

    }






	fragColor = vec4(col,1.0);
}
