float L = 15.;                        // inheritance neighborhood

void mainImage( out vec4 O, vec2 U )
{
    vec3 R  = iResolution;
    O.rgb = U.y ==  R.y - .5
      ? // top line: random colors
        fract(1e4*sin( U.x  * R ) )
      : // inherit from a random neighbor within the line right above
        T( mod( U + vec2( L *( fract(1e4*sin( dot(U,vec2(327,293)) )) - .5 ) , 1 ), R.xy ) ).rgb;
}                                                // why R.xy don't work at res 512 or 640 ?
