vec2 screenUV(vec2 uv) {
    uv /= iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    return uv;
}

float csin(float x)
{
    return (sin(x) + 1.) * 0.5;
}

vec2 xy(float a)
{
    return vec2(cos(a), sin(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = screenUV(fragCoord);

    float d1 = length(uv + 0.01 * xy(iTime));
    float d2 = length(uv + 0.04 * xy(iTime * 1.1));
    float d3 = length(uv);

    float c1 = csin(43. * d1 - 2.3 * iTime);
    float c2 = csin(47. * d2 - 5.1 * iTime);
    float c3 =  sin(17. * d3 - 2.3 * iTime);

    fragColor = vec4(c3 * c3 * vec3(c1, c2, 1.0 - c1 * c2), 1.0);
}
