float Hash21(vec2 p) {
	p = fract(p*vec2(123.34, 456.21));
    p += dot (p, p+45.32);
    return fract(p.x*p.y);
}

float Scene(vec2 uv) {
    vec2 gv = uv;
    float d = length(gv);
    float circle = smoothstep(.201, .2, d);

    gv /= sin(gv.x*50.)*1.5;
    gv.x -= sin(iTime*1.)*.1;
    //uv.x = abs(uv.x*1.3);
    d = length(gv);

    float ant = smoothstep(.28, .24, d);
    float scene = circle + ant;
    circle = smoothstep(.1501, .15, length(uv));
    scene -= circle*2.;

	return clamp(scene, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);

    uv.x += sin(iTime*60.)*.0005;
    uv *= uv.x/uv.y+uv.x*.1;
    uv.x -= .2;
    uv *= .8;

    col.r += Scene(uv);
    col.r += Scene(uv*.8)*.4;
    col.b += Scene(uv*.5)*.1;
    col.g += Scene(uv*.5)*.1;

    float sz = cos(iTime)*.004;
    col.rg += smoothstep(.122+sz, .12+sz, length(uv))*2.;
    col.rgb += smoothstep(.14, .0, length(uv-.02))*.5;

    col.b += col.r * sin(iTime)*.8;

    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv.y += tan(iTime*.5);
    col -= smoothstep(1.4, 0.0, length(uv.y));

    col *= Hash21(vec2(uv.x, uv.x))*.2+.9;

    // vignette
    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    col -= Hash21(uv)*.3;
    uv.x += sin(iTime*60.)*.0005;
    uv *= .6;
    uv.x *= 4.5;
    uv.y -= .5;
    vec3 colOrg = col;
    col *= smoothstep(-.95, .0, uv.y)*1.5;
    col *= smoothstep(0., -.95, uv.y)*1.5;
    col *= smoothstep(-5., 1., uv.x)*1.5;
    col *= smoothstep(5., -1., uv.x)*1.5;
    col = mix(colOrg, col, 0.9);
    col *= 1.04;

    col -= Hash21(uv)*.3;

    fragColor = vec4(col,1.0);
}
