// Control:
// rotate camera(mouse) and move(arrows)
// keyboard Space to switch static/dynamic frame


// what is it:
// BufA - camera control
// BufB - intersected 3D scene (colorRGB dynamic scene, colorA static frame)
// BufC - Inverse bilinear interpolation
// BufD - Homography Screen/CameraUV in QuadUV as QuadUV (UV as colorRG)


// quad texture is Blue, real cube is Red
// orange border and Nyancat texture from BufC
// top right is BufB in BufD UV its quad UV
// top green is frame that used to build texture(saved frame of cube)
// quad texture always use BufD as UV


// BufD include two versionos of Reprojection, Vanishing point version and Image-Wrap 
// Image-Wrap is much slower (performance), can be enabled in Common

// parallelogram case - camera start in parallelogram case

// orange border and Nyancat texture from BufC (parallelogram case fixed)

// quad texture use BufD as UV and BufB(in color.alpha static frame) as texture to display base on UV
// (parallelogram case fixed)



vec4 draw_buf(vec2 fragCoord);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord/iResolution.xy;

    fragColor = texture(iChannel1,uv);
    fragColor = mix(fragColor,texture(iChannel2,uv),texture(iChannel2,uv).a);
    vec4 col=draw_buf(fragCoord);
    fragColor = mix(fragColor,col,col.a);
    fragColor.a=1.;
}

vec4 draw_buf(vec2 fragCoord){
    vec2 res=iResolution.xy/iResolution.y;
    vec2 uv=fragCoord/iResolution.y -res*0.5;
    vec2 ouv=fragCoord/iResolution.xy;
    float size=0.2;
    vec4 retc=vec4(0.);

    // BufB in quadUV(bufD)
    if((abs(uv.x-res.x*0.5+size)<size)&&(abs(uv.y-res.y*0.5+size)<size)){
        uv=uv-.5*res+size*2.;
        vec2 tuv=texture(iChannel3,uv*.5/size).rg;
        retc.rgb=vec3(texture(iChannel1,tuv).a*vec3(0.5,0.8,1.)*2.);
        retc.a=1.;
        //return retc;
    }

    if((abs(ouv.x-1.+size*2.*(1./res.x)+size*0.5)<size*0.5)&&(abs(ouv.y-1.+size*0.5)<size*0.5)){
        ouv=ouv-1.+vec2(size*2.*(1./res.x)+size*0.5,size*0.5);
        retc.rgb=vec3(texture(iChannel1,ouv*1./size+0.5).a*vec3(0.45,1.,.45)*2.);
        retc.a=1.;
        return retc;
    }

    return retc;
}

