bool BoxIntersect( in vec3 ro, in vec3 rd, in mat4 txx, in mat4 txi, in vec3 rad, out float tN, out vec3 norm) 
{
	vec3 rdd = (txx*vec4(rd,0.0)).xyz;
	vec3 roo = (txx*vec4(ro,1.0)).xyz;
    vec3 m = 1.0/rdd;
    vec3 n = m*roo;
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

    norm=vec3(0.,1.,0.);

	if( tN > tF || tF < 0.0) return false;
    if(tN<MIN_DIST && tN>MAX_DIST )return false;

	vec3 nor = -sign(rdd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);

	norm = (txi * vec4(nor,0.0)).xyz;

	return true;
}

bool PlaneIntersect(vec4 Plane, vec3 ro, vec3 rd, out float t, out vec3 norm) {
    norm=vec3(0.,1.,0.);
    t=-1.;
    float dd = dot(rd, Plane.xyz);
    if (dd == 0.0) return false;
    float t1 = -(dot(ro, Plane.xyz) + Plane.w) / dd;
    if (t1 < 0.0) return false;
    norm = normalize(Plane.xyz);
    t = t1;
    return true;
}

bool ParallelogramIntersect( in vec3 ro, in vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2, in vec3 v3, out float tx, out vec3 norm)
{
    vec3 a = v0 - v1;
    vec3 b = v2 - v0;
    vec3 p = v0 - ro;
    vec3 n = cross( a, b );
    vec3 q = cross( rd, p );
    float i = 1.0/dot( rd, n );
    float u = dot( q, a )*i;
    float v = dot( q, b )*i;
    float t = dot( n, p )*i;
    tx=-1.;
    norm=vec3(0.,1.,0.);
    if( u<0.0 || u>1.0 || v<0.0 || v>1.0 ) return false;
    vec3 normx = vec3( t, u, v );
    if( normx.x>MIN_DIST && normx.x<MAX_DIST )
    {
    tx = normx.x;
    vec3 nor = normalize( cross(v2-v1,v1-v3) );
    norm = faceforward( nor, rd, nor );
    return true;
    }
    return false;
}

vec4 calcColor_plane(vec3 ro, vec3 rd, float d)
{
    vec3 p = (ro+rd*d);
    vec2 real_uv=(p.xy+vec2(-pos_x,-pos_y))/(vs*2.)+vec2(0.5,0.5); // real quad UV
    vec2 ssUV=texture(iChannel3,real_uv).rg; // screen space UV reprojection from BufD
    float tc=texture(iChannel1,ssUV).a; // read saved frame in screen space UV
    return vec4(tc*vec3(0.5,0.8,1.)*2.,0.);
}

vec4 texBox(vec3 ro, vec3 rd, float d, vec3 nor, mat4 txx)
{
    vec3 pos = (ro+rd*d);
    vec3 opos = (txx*vec4(pos,1.0)).xyz;
	vec3 onor = (txx*vec4(nor,0.0)).xyz;
    vec3 tc=abs(onor.x)*texture( iChannel2, 0.5*opos.yz ).xyz + 
                   abs(onor.y)*texture( iChannel2, 0.5*opos.zx ).xyz + 
                   abs(onor.z)*texture( iChannel2, 0.5*opos.xy ).xyz;
    return vec4(tc,1.);
}


vec4 calcColor_floor(vec3 ro, vec3 rd, float d) {
   	vec2 p = (ro+rd*d).xz;
    vec2 e = min(vec2(1.0), fwidth(p)); 
    vec2 l = smoothstep(vec2(1.0), 1.0 - e, fract(p)) + smoothstep(vec2(0.0), e, fract(p)) - (1.0 - e);
    return vec4(mix(vec3(0.4), vec3(0.8) * (l.x + l.y) * 0.5, exp(-d*0.01)),0.);
}


#define OBJ_CUBE 1
#define OBJ_FLOOR 2
#define OBJ_WALL 3

struct HitInfo {
    float t;
    vec3 norm;
    vec4 color;
    int obj_type;
};

void ParallelogramIntersectMin(vec3 ro, vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2, inout bool result, 
                               inout HitInfo hit, int OBJ) {
    float tnew;
    vec3 normnew;
    vec3 v3 = v1 + v2 - v0;
    if (ParallelogramIntersect(ro, rd, v0, v1, v2, v3, tnew, normnew)) {
        if (tnew < hit.t) {
            hit.t = tnew;
            hit.norm = normnew;
            float la=hit.color.a;
            hit.color= calcColor_plane(ro,rd,hit.t);
            hit.color.a=la;
            hit.obj_type = OBJ;
        }
        result = true;
    }
}

void GroundIntersectMin(vec3 ro, vec3 rd, inout bool result, inout HitInfo hit) {
    float tnew;
    vec3 normnew;
    vec4 pp=vec4(0.0,01.,0.0,0.);
    if (PlaneIntersect(pp, ro, rd, tnew, normnew)) {
        if (tnew < hit.t) {
            hit.t = tnew;
            hit.norm = normnew;
            hit.color = calcColor_floor(ro,rd,hit.t);
            hit.color.a=hit.color.r*0.25;
            hit.obj_type = OBJ_FLOOR;
        }else hit.color.rgb = bgcol.rgb;
        result = true;
    }
}

void BoxIntersectMin(vec3 ro, vec3 rd, vec3 box, vec3 bpos, float angle, inout bool result, inout HitInfo hit, bool transp) {
    float tnew;
    vec3 normnew;
    mat4 rot = rotationAxisAngle( normalize(vec3(1.0,1.0,0.0)),  angle);
	mat4 tra = translate(bpos);
	mat4 txi = tra * rot; 
	mat4 txx = inverse( txi );
    if(determinant(txi)==0.)txx=txi;
    if (BoxIntersect(ro, rd, txx, txi, box, tnew, normnew)) {
        bool val=false;
        if(transp)val=tnew < MAX_DIST;
        else val=tnew < hit.t;
        if (val) {
            hit.t = tnew;
            hit.norm = normnew;
            if(transp){
                vec3 lc=hit.color.rgb;
                hit.color.a = texBox(ro,rd,hit.t,normnew,txx).r;
                hit.color.rgb=lc;
            }else{
                float la=hit.color.a;
                hit.color.rgb = texBox(ro,rd,hit.t,normnew,txx).r*vec3(1.,0.28,0.15);
                hit.color.a=la;
            }
            hit.obj_type = OBJ_CUBE;
        }
        result = true;
    }
}

bool minDist(vec3 ro, vec3 rd, out HitInfo hit)
{
    vec2 res = vec2(1.);
    hit.t = MAX_DIST;
    hit.color=vec4(bgcol.rgb,0.);
    bool result = false;

    vec4 verts_local [4]=verts;
    verts_local[0].xyz+=vec3(pos_x,pos_y,pos_z);
    verts_local[1].xyz+=vec3(pos_x,pos_y,pos_z);
    verts_local[2].xyz+=vec3(pos_x,pos_y,pos_z);
    verts_local[3].xyz+=vec3(pos_x,pos_y,pos_z);

    GroundIntersectMin(ro, rd, result, hit);

    vec3 box=vec3(pos_x,pos_y,pos_z);
    float angle=0.5;

    ParallelogramIntersectMin(ro, rd, verts_local[1].xyz, verts_local[2].xyz, verts_local[0].xyz, result, hit,OBJ_WALL);
    float refresh=texelFetch(iChannel0,ivec2(iResolution.xy-1.),0).a;
    if(refresh>=-1.5)BoxIntersectMin(ro, rd,vec3(.75),box,angle,result, hit, false);
    BoxIntersectMin(ro, rd,vec3(.75),box,angle,result, hit, true);

    return result;
}

const float eps = 1e-3;

vec4 render(Ray r)
{
    vec3 col = vec3(0.0);
    vec3 objectcolor = vec3(1.0);
    float objcol2=0.;
    vec3 mask = vec3(1.0);
    HitInfo hit;
    hit.color=vec4(0.);
    {
        if(minDist(r.pos, r.dir, hit)){
            objectcolor = hit.color.rgb;
            vec3 p = r.pos + r.dir * hit.t + hit.norm*eps;
            vec3 sunDir = normalize(l1Pos-p);
            col = objectcolor;
            objcol2=hit.color.a;
        }else col = bgcol.rgb;
    }
    return vec4(col,objcol2);
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec4 lastcol=texelFetch(iChannel1,ivec2(fragCoord),0);

    float refresh=texelFetch(iChannel0,ivec2(iResolution.xy-1.),0).a;

    vec4 ret_col = vec4(0.0);
    #if AA>1
    for( int mx=0; mx<AA; mx++ )
    for( int nx=0; nx<AA; nx++ )
    {
    vec2 o = vec2(float(mx),float(nx)) / float(AA) - 0.5;
    vec2 uv = (fragCoord+o)/iResolution.xy * 2.0 - 1.0;
    #else
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    #endif
    uv.y *= iResolution.y/iResolution.x;
    vec4 col = render(SetCamera(uv));
    ret_col += col;
    #if AA>1
    }
    ret_col /= float(AA*AA);
    #endif

    if(refresh>=-1.5){
        lastcol.a=ret_col.a;
    }

    fragColor=vec4(ret_col.rgb,lastcol.a);

}

