// transform 3D verticles to 2D points

#define load(P) texelFetch(iChannel0, ivec2(P), 0)

const ivec2 POSITION = ivec2(1, 0);
const ivec2 VMOUSE = ivec2(2, 0);

mat4 get_camera() {
    vec3 ro = load(POSITION).xyz;
    vec2 m = load(VMOUSE).xy;
    m.y = -m.y;
    mat3 rotX = mat3(1.0, 0.0, 0.0, 0.0, cos(m.y), sin(m.y), 0.0, -sin(m.y), cos(m.y));
    mat3 rotY = mat3(cos(m.x), 0.0, -sin(m.x), 0.0, 1.0, 0.0, sin(m.x), 0.0, cos(m.x));
    mat3 rotM=rotY*rotX;

    mat4 camToWorld = mat4(1.);
    camToWorld[0].xyz = -rotM[0];
    camToWorld[1].xyz = -rotM[1];
    camToWorld[2].xyz = rotM[2];

    camToWorld[3].xyz = ro;
    camToWorld[0].w=0.;
    camToWorld[1].w=0.;
    camToWorld[2].w=0.;

    return camToWorld;
}

mat4 perspectiveMatrix(float fovYInRad, float aspectRatio)
{
    float yScale = 1.0/tan(fovYInRad / 2.0);
    float xScale = yScale / aspectRatio;
    float zf = 100.0;
    float zn = 1.;

    float z1 = zf/(zf-zn);
    float z2 = -zn*zf/(zf-zn);

    mat4 result = mat4(vec4(xScale, 0.0, 0.0, 0.0),
                       vec4(0.0, yScale, 0.0, 0.0),
                       vec4(0.0, 0.0, z1, z2),
                       vec4(0.0, 0.0, -1.0, 0.0));

    return result;
}

void get_points(out vec2 a, out vec2 b, out vec2 c, out vec2 d){

    float aspect = iResolution.x / iResolution.y;
    //aspect=1.;  //when UV(or p) in mainImage include aspect, this not needed

    mat4 perspective=perspectiveMatrix(fov* (M_PI / 180.0),aspect);
    mat4 camera=get_camera();
    mat4 worldMat = inverse(camera);
    mat4 mvp = perspective * worldMat;

    vec4 verts_local [4]=verts;
    verts_local[0].xyz+=vec3(pos_x,pos_y,pos_z);
    verts_local[1].xyz+=vec3(pos_x,pos_y,pos_z);
    verts_local[2].xyz+=vec3(pos_x,pos_y,pos_z);
    verts_local[3].xyz+=vec3(pos_x,pos_y,pos_z);

    vec4 WorldVert=vec4(0.);
    WorldVert = mvp * verts_local[0];
    a=WorldVert.xy / WorldVert.w;
    WorldVert = mvp * verts_local[1];
    b=WorldVert.xy / WorldVert.w;
    WorldVert = mvp * verts_local[2];
    c=WorldVert.xy / WorldVert.w;
    WorldVert = mvp * verts_local[3];
    d=WorldVert.xy / WorldVert.w;
}


// Inverse bilinear interpolation

float cross2d( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

vec2 invBilinear( in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d )
{
    vec2 e = b-a;
    vec2 f = d-a;
    vec2 g = a-b+c-d;
    vec2 h = p-a;

    float k2 = cross2d( g, f );
    float k1 = cross2d( e, f ) + cross2d( h, g );
    float k0 = cross2d( h, e );

    float w = k1*k1 - 4.0*k0*k2;
    if( w<0.0 ) return vec2(-1.0);
    w = sqrt( w );

    // will fail for k0=0, which is only on the ba edge 
    float v = 2.0*k0/(-k1 - w); 
    if( v<0.0 || v>1.0 ) v = 2.0*k0/(-k1 + w);

    float u = (h.x - f.x*v)/(e.x + g.x*v);
    if( u<0.0 || u>1.0 || v<0.0 || v>1.0 ) return vec2(-1.0);
    return vec2( u, v );
}


float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 2.*(fragCoord.xy/iResolution.xy-0.5);

    float refresh=texelFetch(iChannel0,ivec2(iResolution.xy-1.),0).a;

    // background
    vec4 col = vec4( 0.);

    // move points
    vec2 a;
    vec2 b;
    vec2 c;
    vec2 d;
    get_points(a,b,c,d);

    // when lines paralel just move one point
    if((a.x==b.x)&&(c.x==d.x))a.x+=0.001;

    // d = c+a-b; // enable this to test parallelograms

    // area of the quad
    vec2 uv = invBilinear( p, a, b, c, d );
    if( uv.x>-0.5 )
    {
        col = texture( iChannel1, uv.yx*vec2(1./6.,1.) );
        float td=0.;
        if(refresh<.0)td=smoothstep(0.,1.,mod(iTime+10.,20.))*(1.-smoothstep(7.,8.,mod(iTime+10.,20.)));
        col.a*=0.2+0.8*td;
    }

    // quad borders
    float h = 2.0/iResolution.y;
    float td=1.;
    td=min(td,smoothstep(h,2.0*h,sdSegment(p,a,b)));
    td=min(td,smoothstep(h,2.0*h,sdSegment(p,b,c)));
    td=min(td,smoothstep(h,2.0*h,sdSegment(p,c,d)));
    td=min(td,smoothstep(h,2.0*h,sdSegment(p,d,a)));
    if(td<1.)
    col = vec4(vec3(1.0,0.7,0.2),1.-td);

	fragColor = col;
}

