// Reprojection

#ifdef use_vpoint

// version 1 
// using Homography/Vanishing point

// using http://alienryderflex.com/intersect/
vec2 lineIntersection(vec2 a1, vec2 a2, vec2 b1, vec2 b2){
  vec2 ret=vec2(100000.);
  vec2 ob1=b1;
  vec2 ob2=b2;
  if ((a1==a2) || (b1==b2)) return ret;
  a2+=-a1;
  b1+=-a1;
  b2+=-a1;
  float distAB=length(a2);
  vec2 rot=a2/distAB;

  float newX=b1.x*rot.x+b1.y*rot.y;
  b1.y  =b1.y*rot.x-b1.x*rot.y; b1.x=newX;
  newX=b2.x*rot.x+b2.y*rot.y;
  b2.y  =b2.y*rot.x-b2.x*rot.y; b2.x=newX;

  //when lines paralel return far point in line2 direction
  if (b1.y==b2.y) return 100000.*(ob2-ob1);

  float ABpos=b2.x+(b1.x-b2.x)*b2.y/(b2.y-b1.y);
  ret=a1+ABpos*rot;
  return ret; 
}

// https://iquilezles.org/articles/intersectors
float plaIntersect( in vec3 ro, in vec3 rd, vec4 p)
{
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

vec3 resolveCorner(vec3 p1, vec2 p1_r, vec2 p2_a, vec2 pl_a, vec2 pl_b)
{
    vec2 intersec = lineIntersection(p1_r,p2_a,pl_a,pl_b);
    vec3 vp = normalize(vec3(-intersec,1.));
    vec3 p1_to_p2 = vp;
    vec3 r2 = normalize(vec3(-p2_a,1.));

    vec3 n_cam_p1_p2 = cross(p1_to_p2,r2);
    vec3 n_plane_p2 = cross(n_cam_p1_p2,r2);
    float t = plaIntersect(p1,p1_to_p2,vec4(n_plane_p2,0.));
    vec3 p2_ws = p1+t*p1_to_p2;
    return p2_ws;
}

void resolvePerspective(vec2 sa, vec2 sb, vec2 sc, vec2 sd, 
                        out vec3 wa, out vec3 wb, out vec3 wc, out vec3 wd)
{
    wa = normalize(vec3(-sa,1.));
    wb = resolveCorner(wa, sa, sb, sc, sd);
    wc = resolveCorner(wb, sb, sc, sa, sd);
    wd = resolveCorner(wa, sa, sd, sb, sc);
}

vec2 inversePerspective_uv(vec2 uv)
{
    vec3 wa, wb, wc, wd;
    vec2 a, b, c, d;
    get_points(a,d,c,b);
    resolvePerspective(a*.5, b*.5, c*.5, d*.5, wa, wb, wc, wd);
    vec3 x_ws = wb-wa;
    vec3 y_ws = wd-wa;
    vec3 p_ws = wa+uv.x*x_ws + uv.y*y_ws;
    vec2 puv = -vec2(p_ws.x/p_ws.z,p_ws.y/p_ws.z);

    //formula from https://www.shadertoy.com/view/Wlccz2 (not perfectly corret)
    //puv= a*.5+(b*.5-a*.5)*uv.x+((d*.5-a*.5)*(1.-uv.x)+(c*.5-b*.5)*(uv.x))*uv.y;
    return puv+vec2(0.5,0.5);
}
