// version 2 
// image warp
// this code come from old Java version of image wraping

void LUDecomposition (out int piv[8],inout float LU[8*8]) {
    for (int i = 0; i < 8; i++) {
        piv[i] = i;
    }
    float LUcolj[8]=float[](0.,0.,0.,0.,0.,0.,0.,0.);

    for (int j = 0; j < 8; j++) {
        for (int i = 0; i < 8; i++) {
            int idx=j+i*8;
            LUcolj[i] = LU[idx];
        }
        for (int i = 0; i < 8; i++) {
            int kmax = min(i,j);
            float s = 0.0;
            for (int k = 0; k < kmax; k++) {
                s += LU[k+i*8]*LUcolj[k];
            }

            LU[j+i*8] = LUcolj[i] -= s;
        }
        int p = j;
        for (int i = j+1; i < 8; i++) {
            if (abs(LUcolj[i]) > abs(LUcolj[p])) {
                p = i;
            }
        }
        if (p != j) {
            for (int k = 0; k < 8; k++) {
                int idx=k+p*8;
                int idxl=k+j*8;
                float t = LU[idx]; LU[idx] = LU[idxl]; LU[idxl] = t;
            }
            int k = piv[p]; piv[p] = piv[j]; piv[j] = k;
        }
        int idx=j+j*8;
        if (bool(int(j < 8) & int(LU[idx] != 0.0))) {
            for (int i = j+1; i < 8; i++) {
                int idxl=j+i*8;
                int idxg=j+j*8;
                LU[idxl] /= LU[idxg];
            }
        }
    }
}

bool isNonsingular (in float LU[8*8]) {
    for (int j = 0; j < 8; j++) {
        int idx=j+j*8;
        if (LU[idx] == 0.)
            return false;
    }
    return true;
}

bool solve (in int piv[8],in float LB[8],in float LU[8*8], out float X[8]) {
    if (!isNonsingular(LU)) {
      return false;
    }
    for (int i = 0; i < 8; i++) {
            X[i] = LB[piv[i]];
    }
    for (int k = 0; k < 8; k++) {
        for (int i = k+1; i < 8; i++) {
            	int idx=k+i*8;
                X[i] -= X[k]*LU[idx];
        }
    }
    for (int k = 8-1; k >= 0; k--) {
            int idx=k+k*8;
            X[k] /= LU[idx];
        for (int i = 0; i < k; i++) {
            	int idx=k+i*8;
                X[i] -= X[k]*LU[idx];
        }
    }
    return true;
}

vec2 imageWarp_uv(vec2 uv)
{
    vec2 A, B, C, D;
    get_points(A,D,C,B);
    A*=0.5;B*=0.5;C*=0.5;D*=0.5;
    A+=0.5;B+=0.5;C+=0.5;D+=0.5;
    vec2 a, b, c, d;
    a=vec2(0.);
    b=vec2(1.,0.);
    c=vec2(1.,1.);
    d=vec2(0.,1.);

    float LU[8*8]=float[](
        a.x,a.y,1.,0.,0.,0.,-a.x*A.x,-a.y*A.x,
        b.x,b.y,1.,0.,0.,0.,-b.x*B.x,-b.y*B.x,
        c.x,c.y,1.,0.,0.,0.,-c.x*C.x,-c.y*C.x,
        d.x,d.y,1.,0.,0.,0.,-d.x*D.x,-d.y*D.x,
        0.,0.,0.,a.x,a.y,1.,-a.x*A.y,-a.y*A.y,
        0.,0.,0.,b.x,b.y,1.,-b.x*B.y,-b.y*B.y,
        0.,0.,0.,c.x,c.y,1.,-c.x*C.y,-c.y*C.y,
        0.,0.,0.,d.x,d.y,1.,-d.x*D.y,-d.y*D.y);

    float LB[8]=float[](A.x,B.x,C.x,D.x,A.y,B.y,C.y,D.y);
    int piv[8];
    LUDecomposition(piv,LU);


    float LC[8];
    vec2 tuv=vec2(0.);
    if(solve(piv,LB,LU,LC))
    {
        tuv.x=(LC[0]*uv.x+LC[1]*uv.y+LC[2])/(LC[6]*uv.x+LC[7]*uv.y+1.0001);
        tuv.y=(LC[3]*uv.x+LC[4]*uv.y+LC[5])/(LC[6]*uv.x+LC[7]*uv.y+1.0001);
    }

    return tuv;
}

