void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float refresh=texelFetch(iChannel0,ivec2(iResolution.xy-1.),0).a;
    if(refresh<-1.5){
        fragColor=texelFetch(iChannel3,ivec2(fragCoord),0);
        return;
    }

    vec2 uv=(fragCoord/iResolution.xy);

    #ifdef use_vpoint
    //version 1
    vec2 tuv=inversePerspective_uv(uv);
    #else
    //version 2
    vec2 tuv=imageWarp_uv(uv);
    #endif



    fragColor = vec4( tuv,0.,0. );
}

