#define num_dots 50
#define step_size 400.0
#define step_width 1000.0

const vec3 colors[3] = vec3[3] (
    vec3( 1.0, 0.0, 0.0  ),
    vec3( 0, 1.0, 0.0 ),
    vec3( 0, 0.0, 1.0 )
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 sc = vec2( 0.5, 0.5*iResolution.y/iResolution.x );
    vec2 pos = fragCoord/iResolution.x - sc;

    vec2 dots[ num_dots ];

    float charge = 0.0;
    float g = iTime*0.012 + 0.89;
    float f = iTime*0.0452 + 0.389;
    for( int idx=0; idx<num_dots;idx++ ) 
    {
        vec2 charge_pos  = vec2(sin( iTime*0.823+float(idx)*g )*0.417,
                            sin( iTime*0.5125+float(idx)*f)*0.25);
        float chr = 2.0+sin( iTime*1.4231 + float(idx)*0.9232)*2.0;                            
        charge = charge + chr / dot( pos-charge_pos, pos-charge_pos);
    }    

    vec3 final_color = vec3( 0.0, 0.0, 0.0 );
    for( int c=0; c<3; c ++ ) 
    {
        float intensity = smoothstep( 1000.0 + float(c)*step_size, 
                1000.0 + float(c)*step_size + step_width, charge);

        final_color = final_color + colors[ c ]*intensity;
    }
    fragColor = vec4(final_color,1.0);
}
