void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);
}

#define _Size 0.2
#define _Radius 1.5
#define _Speed 10.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.x;


    fragColor = texture(iChannel0,fragCoord/iResolution.xy);

   	float distMask = 0.;
    vec2 center = vec2(0.);
    float size = _Size*fract(iTime/20.);
    float cent = ((cos(iTime*_Speed/15.)+1.)*0.5)*_Radius;
    vec4 initialT = vec4(cent,0.4,0.0,1.0);
    float erase = fract(iTime/20.)>0.95 ? 50. : 0.02;

    erase *= iTimeDelta;
    cent *= 0.3;
    center.x = sin(iTime*_Speed)/2.;
    center.y = cos(iTime*_Speed)/2.;
    center *= cent;

    distMask = distance(uv, center);
    fragColor = distMask < size && fract(iTime/20.)<0.75 ? mix(initialT,fragColor,distMask)*iTimeDelta+fragColor : max(vec4(0.),fragColor-erase);

}
