void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec4 exit = texture(iChannel0,uv);

    // Output to screen
    fragColor = exit;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;
	uv.y += sin(iTime)/100.;
    uv.x += cos(iTime)/100.;
    vec4 c1 = texture(iChannel0, uv);
    vec4 bfA = texture(iChannel1, uv);


    //Thanks to FabriceNeyret2 for the suggestion!
    vec4 cb = normalize(1e-5+fract(c1+bfA)); 

    //vec4 cb = normalize(fract(c1+bfA));  

    fragColor = mix(cb,c1,0.3);
}
