void mainImage( out vec4 O, vec2 u )
{
    vec2 res = iResolution.xy,
          z = ( u* 2. - res) / min(res.x, res.y);

    float t = iTime;
    float scale = 2.;
    z *= scale;

    vec2 a = vec2(sin(t), cos(t));
    vec2 b = vec2(1., 0.);
    vec2 c = vec2(0., 1.);
    vec2 d = vec2(1., 0.);
    vec2 e = vec2(sin(t* 0.83), cos(t* 0.83));


    float p = length(cpow(z, 100) + cmul(a, cpow(z, 4)) + cmul(b, cpow(z, 3))
                     + cmul(c, cpow(z, 2)) + cmul(d, cpow(z, 1)) + e);

    O = vec4(vec3(0.2 / p), 1.);
}

vec2 cmul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

vec2 cpow(in vec2 a, in int n) {
    vec2 z = a;
    for (int i = 1; i < n; i++) {
        z = cmul(z, a);
    }
    return z;
}
