#ifdef GL_ES
precision mediump float;
#endif
void mainImage(out vec4 fragColor,in vec2 fragCoord) 
{
  vec2 coord = 4.0 * fragCoord.xy /iResolution.xy;
  for (int n=1; n<118; n++) 
  {
    float i = float(n);
    coord += vec2(0.7 / i*sin(i*coord.y + iTime*.95 + 0.3*i) + 0.8, 0.04 / i*tan(coord.x+tan(iTime*.015) + 0.63*i+cos(iTime*.51)) + 1.6);
  }
 vec3 color = vec3(0.5 * tan(coord.x) + 0.5, 0.5*tan(coord.y) + 0.5, tan(coord.x + coord.y));
  fragColor = vec4(color, 1.0);
}
