#define N 256

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = 2.0 * fragCoord/iResolution.y - 1.0;
    c.x -= 1.0;
    vec2 z = vec2(0.0, 0.0);

    fragColor = vec4(0.0);
    int i = 0;
    for(i = 0; i < N; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if(length(z) > 2.0)
        {
            fragColor = vec4(1.0);
            break;
        }
    }
}
