#define dif vec3(1./iResolution.xy,0.)
#define Smp(uv) texture(iChannel0, uv)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*dif.xy;

    uv = (
        Smp(uv + dif.xz).xy + 
        Smp(uv - dif.xz).xy + 
        Smp(uv + dif.zy).xy + 
        Smp(uv - dif.zy).xy)*(sin(iTime -0.7) + 1.)*0.5;

    fragColor = Smp(uv);
}
