// Code by Flopine

// Thanks to wsmind, leon, XT95, lsdlive, lamogui, 
// Coyhot, Alkama,YX, NuSan and slerpy for teaching me

// Thanks LJ for giving me the spark :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  https://twitter.com/CookieDemoparty


#define iTime mod(iTime,1.5)
#define hr vec2(1.,sqrt(3.))
#define hdist(puv,r) (max(abs(puv.x), dot(abs(puv),normalize(hr)))-r)

float grid (vec2 uv)
{
    vec2 ga = mod(uv, hr)-hr*0.5;
    vec2 gb = mod(uv-hr*0.5, hr)-hr*0.5;
    vec2 guv = (dot(ga,ga)<dot(gb,gb)) ? ga : gb;

    return smoothstep(0.01,0.12, abs(hdist(guv,0.42)))*(hdist(guv,0.02)*1.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float g = grid((uv+grid(uv*2.))*0.5);
    vec3 col = mix(vec3(0.6,0.8,0.95),vec3(0.,0.2,0.5),g);

    uv.x -= mix(-3.,3.,iTime*1.5);
    float line = smoothstep(0.4,g,abs(uv.x-uv.y));
    col += line*0.3;

    fragColor = vec4(clamp(col,0.,1.),1.0);
}
