#define PI 3.1415926

vec2 opTransform( in vec2 p, in float angle)
{
    vec2 r1 = vec2(cos(angle), -sin(angle));
    vec2 r2 = vec2(sin(angle), cos(angle));
    return vec2(dot(p,r1), dot(p,r2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xx;

    // Time varying pixel color
    float freq_1 = 500.0 * PI;
    float density_1 = (0.5*sin(freq_1 * uv.x + iTime)+0.5);
    vec3 col1 = vec3(density_1);

    float freq_2 = 501.0 * PI;
    float density_2 = (0.5*sin(freq_2 * opTransform(uv, 0.05*PI).x)+0.5);
    vec3 col2 = vec3(density_2);

    // Output to screen
    fragColor = vec4(col1*col2,1.0);
}
