void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;        

	vec2 p = uv*64.0; // "64" This should be the input texture size
    //vec2 p = fragCoord.xy;

    vec2 c = floor(p)+0.5; //center of the pixel i.e. [33.5, 94.5]

    vec2 dist = p - c; //How far are we from the center of the pixel

    dist = 16.0*dist*dist*dist*dist*dist; //shift towards the center of the pixel
    //dist = 4.0*dist*dist*dist; //shift towards the center of the pixel


    p = (c + dist);

    fragColor = texture(iChannel0,p/64.0); //"64" should be the input texture size.

}
