float dist(vec2 p) {
    p = abs(p);

    float c = dot(p, normalize(vec2(.1, .1)));
    c = max(c*0.95, p.y*.75);

    return c;
}

vec4 coords(vec2 uv) {
    vec2 a = fract(uv)-.5;
    vec2 b = fract(uv-.5)-.5;

    vec2 gv;

    if (length(a) <  length(b))
   	    gv = a;
    else
        gv = b;

    vec2 id = uv - gv;

    return vec4(0, dist(gv), id.x, id.y); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.x;

	vec3 col = vec3(0);

    uv *= 8.;

    vec4 c = coords(uv);

    float v;

	c.z -= mod(floor(iTime*6.), 12.);

    if ((-c.z + iTime == abs(c.w) + iTime))

    	v = abs(c.y+sin(c.z * c.w + iTime*0.3 ));

	else
        v = c.y ;

    float s = smoothstep(.33, .33, v);

    col += s;

    fragColor = vec4(col, 0);
}
