void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float R = smoothstep(-0.0,1.3,sin( uv.x*20.0)*0.5+sin(uv.y*12.0)*0.5+0.5);
    float G = smoothstep(-0.8,2.8,sin( uv.x*27.0)*0.5+sin(uv.y*10.0)*0.5+0.5);
    float B = smoothstep(-0.9,2.9,sin( uv.x*30.0)*0.5+sin(uv.y*9.0)*0.5+0.5);
    col = vec3(R,G,B);

    // Output to screen
    fragColor = vec4(col+vec3(0.5),1.0);
}
