 * Burning-Ship Fractal: https://www.shadertoy.com/view/wljSDt
 * Forest Fractal: https://www.shadertoy.com/view/tt2SDt
 * Crown Fractal: https://www.shadertoy.com/view/tl2SDt
 */

#define AA 4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p0 = vec2(0.0,1.285023);
    vec2 p1 = vec2(0.0,0.4);
    float m1 = 6.0/length(iResolution), m0 = 1e-5*m1;
    float t = 1.0-1.0/(exp(3.0-iTime)+1.0);
    vec2 p = mix(p0,p1,t);
    float m = mix(m0,m1,t);
    int MAX_ITER = int(min(floor(0.5/m),2000.0));

    vec3 col=vec3(0,0,0);
    for (int u=0;u<AA;u++) {
      for (int v=0;v<AA;v++){
        vec2 z = vec2(0,0);
        vec2 c = (fragCoord+vec2(u,v)/float(AA)-0.5*iResolution.xy)*m+p;
        for (int i=0;i<MAX_ITER;i++){
            z=vec2(z.x*(z.x*z.x-3.0*z.y*z.y),-abs(z.y*(3.0*z.x*z.x-z.y*z.y)))+c;
            float h = dot(z,z);
            if (h>2.8243e+11){
                float n = float(i)-log(0.5*log(h)/log(3.0))/log(3.0)+4.0;
                float m = exp(-n*n/20000.0);
                n = mix(4.0*pow((log(n+1.0)+1.0),2.0),n,m);
              #if 1
                  m = 5.0*sin(0.1*(n-6.0))+n;
                  col += vec3(
                      pow(sin((m-8.0)/20.0),6.0),
                      pow(sin((m+1.0)/20.0),4.0),
                      (0.8*pow(sin((m+2.0)/20.0),2.0)+0.2)*(1.0-pow(abs(sin((m-14.0)/20.0)),12.0))
                  );
              #else
                  col += vec3(
                      pow(sin((n-6.0)/20.0),6.0),
                      pow(sin((n-2.0)/20.0),4.0),
                      0.8*pow(sin((n+2.0)/20.0),2.0)+0.2-0.2*pow(abs(sin((n-18.0)/20.0)),6.0)
                  );
              #endif
                break;
            }
        }
      }
    }
    col/=float(AA*AA);
    fragColor=vec4(col,1.0);
}
