#define PI 3.14159265359
#define PI2 6.28318530718
#define E 2.7182818284
#define S01(t) sin(t) * .5 + .5
#define C01(t) cos(t) * .5 + .5
#define S(a,b,t)  mix(a, b, S01(t))
#define C(a,b,t)  mix(a, b, C01(t))
#define BPM 93.
#define BPS BPM/60.

float N21(vec2 p){
    p = fract(p*vec2(234.56, 124.));
    p += dot(p, p+23.45);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p){
    float n = N21(p);
    return vec2(n, N21(p+n));
}

float lineDF(vec2 a, vec2 b, vec2 p) {
    vec2 ap = p-a;
    vec2 ab = b-a;
    float t = clamp(dot(ap,ab)/dot(ab,ab),0.,1.);
    vec2 c = a+t*ab;
    return length(p-c);
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = ( 2.*u - R ) / R.y;

    float t = iTime *BPS;
    float t1 = floor(t);
    float n1 = N21(U.xx+t1);
    float n2 = N21(U.yy+floor(t*3.));
	float v = 0.;
    v += step(.9,n1);
    v += step(.9,n2);

	vec3 col = vec3(v);
	O = vec4(col,1);
}
