void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    // Output to screen
   fragColor = vec4(0.3,0.3,0.3,1);


     if(uv.x>(cos(iTime+uv.y)+3.0)/10.0)
    {
        fragColor = vec4(1,.8,0,1)  ;
    }


    if(uv.x>(cos(iTime+uv.y)+3.5)/10.0)
    {
        fragColor = vec4(1,1,1,1)  ;
    }



    	if(uv.x>(cos(iTime+uv.y)+4.5)/10.0)
    {
        if(uv.x>(cos(iTime+uv.y)+5.0/10.0))

    		if(mod(uv.y*10.0,2.0) >0.9){

       		 fragColor = vec4(0,0,0,1);

    }


    }



        if(uv.x>(cos(iTime+uv.y)+4.7)/10.0)
    {
        fragColor = vec4(1,1,1,1)  ;
    }




    if(uv.x>(cos(iTime+uv.y)+5.5)/10.0)
    {
        fragColor = vec4(1,.8,0,1)  ;
    }

       if(uv.x>(cos(iTime+uv.y)+6.0)/10.0)
    {
        fragColor = vec4(0.3,0.3,0.3,1)  ;
    }





}
