vec3 bolt( in vec2 uv, in float speed, in float freq )
{
    vec3 col = vec3(0.0);
    for ( float i=0.0; i<0.05; i+=0.01 )
    {
        vec2 nuv = uv;
    	nuv.x += 0.25*(0.5-texture(iChannel1,vec2((iTime-i)*speed,nuv.y*freq)).x)*pow(0.5-abs(0.5-uv.y),0.5);
		col += 0.5*texture(iChannel0,nuv).rgb;
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0);
    col += bolt(uv,0.53534,0.21);
    col += bolt(uv,0.64563,0.22);
    col += bolt(uv,0.73425,0.23);
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.25,0.5,1.0)*max(0.0,1.0-pow(32.0*abs(0.5-uv.x),0.15));
    fragColor = vec4(col,1.0);
}
