void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    float scrollA = texture(iChannel1, 0.2*uv + iTime*vec2(0.004, 0.01)).y;
    float scrollB = texture(iChannel2, 0.4*uv + iTime*vec2(-0.02, 0.01)).x;

    vec2 swimA = scrollA*vec2(0.01, 0.025);
    vec2 swimB = scrollB*vec2(-0.02, 0.01);

    vec2 swim = swimA*20. + swimB*30.;
    swim *= 0.1;

    vec3 col = texture(iChannel0, uv + swim).xyz;
    //col = vec3(scrollB);
    fragColor = vec4(col,1.0);
}
