//Attempt at smoothing the animations of iq's cheap noise using sine distortion.
//Also trying to making the noise pattern look more random
//click the mouse to see without smoothing of the animation
//press keys 1 2 3 to see the different noises full screen

#define M_PI 3.14159265359

mat2 rot(float a){
return mat2(
  cos(a), sin(a),
  -sin(a), cos(a)
  );
}

float noise31(vec3 p){

    vec3 id = floor(p);
    vec3 fd = fract(p);

    fd = fd*fd*(3.-2.*fd);

    vec2 rOffset = vec2(37.,17.)*id.z;
    vec2 uv = id.xy + rOffset + fd.xy;
    vec2 texNoise = texture(iChannel0, (uv+0.5)/256.0).yx;

    float n = mix(texNoise.x,texNoise.y,fd.z);  

    return n;
}

float noise31_sine(vec3 p){
  vec2 offset = sin(p.xy*vec2(1.876689))*vec2(.310876);
  p.z+=offset.x+offset.y; 
  float n = noise31(p);
  return n;

}

float noise(vec3 p){
  float n = 0.;

  if(iMouse.z <= 0.){
    n = noise31_sine(p);
  }else{
    n = noise31(p);
  }

  return n;  
}

float fbm(vec3 p){
  float G = exp(-1.);
  float amp = 1.;
  float freq = 1.;
  float n = 0.;

  for(int i = 0; i <8; i++){
    p.xy*=rot(0.2*M_PI);
    n+= noise(p*freq)*amp;
    freq*=2.;
    amp*=G;
  }

  return n*0.7;
}

float warp(vec3 p){
     float n = 0.;

      vec3 q = vec3(
           fbm(p + vec3(0.,0.,0.0)),  
           fbm(p + vec3(2.1,1.5,2.1)),  
           fbm(p + vec3(1.5,3.4,2.1))
           );
      vec3 r = vec3(
           fbm(q + vec3(2.1,3.5,1.5)),  
           fbm(q + vec3(1.1,0.7,1.1)),  
           fbm(q + vec3(0.2,1.4,2.3))
           );

      return n = fbm(vec3(p + r*4.));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float grad = fragCoord.x/iResolution.x;
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    //keyboard input
    float one = texelFetch( iChannel1, ivec2(49,0), 0 ).x;
    float two = texelFetch( iChannel1, ivec2(50,0), 0 ).x;
    float three = texelFetch( iChannel1, ivec2(51,0), 0 ).x;

    float scale = 5.;
    float speed = 0.5;
    vec3 p = vec3(uv*scale,iTime*speed);

    float n = 0.;

    if(one > 0.){
      n = noise(p);    
    }else if(two > 0.){
      n = fbm(p);        
    }else if(three > 0.){
      n = warp(p);
    }else{   
      if(grad < 0.325){
        n = noise(p);
      }else if(grad > 0.33 && grad < 0.655){
        n = fbm(p);    
      }else if(grad > 0.66){
        n = warp(p);       
      }
    }

    fragColor = vec4(n);
}
