void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // colour palette
    vec3 c1 = vec3(248, 177, 149) / 255.0;
    vec3 c2 = vec3(246, 114, 128) / 255.0;
    vec3 c3 = vec3(192, 108, 132) / 255.0;
    vec3 c4 = vec3(108, 91, 123) / 255.0;
    vec3 c5 = vec3(53, 92, 125) / 255.0;

    vec2 centre1 = vec2(sin(iTime / 3.0) * 0.5 + 0.5, 0.5);
    vec2 centre2 = vec2(sin(iTime / 5.7) * 0.5 + 0.5, 0.3);
    vec2 centre3 = vec2(0.7, cos(iTime / 8.0) * 0.5 + 0.5);
    vec2 centre4 = vec2(sin(iTime / 1.5) * 0.5 + 0.5, cos(iTime / 1.1) * 0.5 + 0.5);
    vec2 centre5 = iMouse.xy / iResolution.xy;

    float total = 0.0;

    total += 1.0 / length(uv - centre1);
    total += 2.0 / length(uv - centre2);
    total += 1.0 / length(uv - centre3);
    total += 0.6 / length(uv - centre4);
    total += 0.6 / length(uv - centre5);

    total = total / 20.0;

    vec3 col = vec3(0.0);

    if (total < 0.6) {
    	col = c1;
    } else if (total < 0.7) {
        col = c2;
    } else if (total < 0.8) {
        col = c3;  
    } else if (total < 0.9) {
        col = c4;
    } else {
    	col = c5;
    }




    // Output to screen
    fragColor = vec4(col,1.0);
}
