vec2 radial (vec2 uv)
{
    float r = 1.,                                   
        n = 1.+30.*(.5+.5*sin(iTime)),            
        l = length(uv),                             
        a = atan(uv.y,uv.x),
        u = r * ( fract( a*n/6.283 ) - .5 ) / (n/6.283),
        v = l - r;    
    return vec2(u,v);

}

void mainImage( out vec4 col, in vec2 uv )
{
    vec2 res = iResolution.xy;
    uv = ( 2.*uv - res ) / res.y;                           
    uv *= 1.;
    uv = radial(uv);

    vec3 c = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = vec4(c,1.0);
}
