float rectangle(vec2 c, vec2 wh, vec2 uv)
{
    uv -= c;    
    vec2 res = step(-wh / 2.0, uv) * (1.0 - step(wh / 2.0, uv));
    return res.x * res.y;
}

float circle(vec2 c, float r, vec2 uv)
{  
    return smoothstep(r + 0.01, r, length(uv));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;

    float aspect = iResolution.x / iResolution.y; 
    uv.x *= aspect; 

    float width = (smoothstep(0., 1.,abs(sin(iTime / 10.))) + 0.2) * 0.05;
    float wStep = width / 3.;

    vec3 col = vec3(.0);


    vec2 center = vec2(.5 * aspect, .5);
    uv = abs(uv) - center;

    for(float r = mod(iTime / 4., 2.) + 2.; r >= 0.; r -= width)
    {    
        float cir = circle(center, r, uv); 

        float blackAmount = (width - 0.01) * abs(sin(iTime * 3.0));
        float div = 1.0 / (width - blackAmount);
        float len = (length(uv) - (r - (width - blackAmount))) * div;
        vec3 rimColor = vec3(len);
        col = mix(col, rimColor, cir); 
    }    

    fragColor = vec4(col, 1.0);

}
