void mainImage( out vec4 f, in vec2 g )
{
    f = vec4(.8,.2,.5,1);
    vec2 s = iResolution.xy;
	vec4 h;
   	g = (g+g-s)/s.y*1.3;
    vec2 
        k = vec2(1.6,0) + mod(iTime,6.28), 
        a = g - sin(k),
    	b = g - sin(2.09 + k),
    	c = g - sin(4.18 + k);
    f = (0.02/dot(a,a) + 0.02/dot(b,b) + 0.02/dot(c,c)) * 0.04 + h * 0.96 + step(h, f) * 0.01;
}
