void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
	vec2 q = p - vec2(0.3,0.7);
    vec2 q2 = p - vec2(0.7,0.5);
    vec2 sol = p- vec2(0.5,0.01);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

	vec3 col = mix( vec3(1.,0.5,0.3), vec3(0.3,0.6,.9), p.y );
    //col *= uv.y;	// horizontal gradient
    //col *= length( q );
    col += 1.-smoothstep(0.01,0.3,length( sol ));

    // palm 1
    float r = 0.2 + 0.1*cos( atan(q.y,q.x)*10.0 +20.*q.x+3.*q.y );
    col *= smoothstep(r,r+0.01,length( q ));

    r = 0.01; //+ 0.01*cos(10.*q.x);
    r += 0.002*cos(100.*q.y);
    r += exp(-50.*p.y);
    float we = smoothstep(r,r+0.001,abs( q.x -0.25*sin(2.*q.y) ));	// tronco
    col *= 1.-(1.-we)*(1.-smoothstep(0.,0.01,q.y));	// borra parte de arriba del tronco

    // palm 2
    r = 0.05 + 0.05*cos( atan(q2.y,q2.x)*10.0 +50.*q2.x-10.*q2.y );
    col *= smoothstep(r,r+0.01,length( q2 ));

    r = 0.005; //+ 0.01*cos(10.*q.x);
    r += 0.001*cos(100.*q2.y);
    r += exp(-50.*p.y);
    we = smoothstep(r,r+0.001,abs( q2.x +0.15*sin(2.*q2.y) ));	// tronco
    col *= 1.-(1.-we)*(1.-smoothstep(0.,0.01,q2.y));	// borra parte de arriba del tronco

    // Output to screen
    fragColor = vec4(col,1.0);
}
