float stepAA(float y, float x)
{
    x -= y;
    return clamp(x / fwidth(x) + 0.5, 0.0, 1.0);
}

float d(vec2 uv)
{
    return max(abs(length(uv)-0.056) - 0.014, -min(min(min(abs(uv.x)-0.0008, abs(uv.y)-0.0007), abs(uv.x - uv.y)*0.75-0.001), abs(uv.x + uv.y) * 0.75-0.001));
}

mat2 rot(float a)
{
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col;

    float d = d(uv);
    uv *= rot(round(-iTime * 12.0) * 6.283185 / 8.0);
    float polar = atan(uv.x, uv.y) / 6.283185 + 0.5;

    d = stepAA(0.0, -d - 0.002);
    col += d * 0.5;
    col += step(0.0, -polar + 1.0 / 8.0) * d * 0.5;

    fragColor = vec4(col, 1);
}
