Main {
    vec4 bb = B(U), cc = C(U),
         a = A(bb.xy), b = A(cc.xy);
	Q = smoothstep(5.,6.,length(U-a.xy))*vec4(1);
	Q *= smoothstep(0.,1.,sg(U,a.xy,b.xy))*vec4(1);
	Q *= smoothstep(1.,2.,mp(U,a.xy,b.xy)*length(a.xy-b.xy))*vec4(1);
	Q.xy *= 0.5+0.5*cc.xy/R;
    Q.yz *= 0.5+0.5*bb.xy/R;

}

#define R iResolution.xy
#define A(U) texture(iChannel0,floor(U)/R)
#define B(U) texture(iChannel1,floor(U)/R)
#define C(U) texture(iChannel2,floor(U)/R)
#define D(U) texture(iChannel3,floor(U)/R)
#define Main void mainImage (out vec4 Q, vec2 U)
#define Init if (iFrame < 1) 
#define Border if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
float sg (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	return (length(p-a-(b-a)*i));
}
float mp (vec2 p, vec2 a, vec2 b) {
	vec2 m = 0.5*(a+b);
    if (length(a-b)<1e-3) return 1e3;
    float o = (dot(p-m,b-m))/dot(b-m,b-m);
	float x = abs(o*length(b-m)), y = length(p-m-(b-m)*clamp(o,-1.,1.));
	return x/length(a-b);
}

// Information Storage
Main {
	Q = A(U);

    if (iMouse.z<1.) Q.xy += .25*Q.zw+.1*sin(.1*Q.yx*vec2(-1,1));


    if (Q.x<2.) {Q.x = 2.; Q.z *= -1.;}
    if (R.x-Q.x<2.) {Q.x = R.x-2.; Q.z *= -1.;}
    if (Q.y<2.) {Q.y = 2.; Q.w *= -1.;}
    if (R.y-Q.y<2.) {Q.y = R.y-2.; Q.w *= -1.;}


    Init {
    	U = floor(U/50.+0.5)*50.;
        Q = vec4(U,1,1);
    }
}

// Nearest Individual
void X (inout vec4 Q, inout vec4 a, vec2 U, vec2 r) {
    vec4 b = B(U+r);
    vec4 n = A(b.xy);
    if (length(n.xy-U)<length(a.xy-U)) {
    	Q = b;
        a = n;
    }
}
void Xr (inout vec4 Q, inout vec4 a, vec2 U, float r) {
	 X(Q,a,U,vec2(r,0));
     X(Q,a,U,vec2(0,r));
     X(Q,a,U,vec2(0,-r));
     X(Q,a,U,vec2(-r,0));
}
Main {
	Q = B(U);
    vec4 a = A(Q.xy);
    Xr(Q,a,U,1.);
    Xr(Q,a,U,2.);
    Xr(Q,a,U,4.);
    Xr(Q,a,U,8.);
    Init {
    	Q = A(U);
    }
}

// Nearest Normalized Bisector
void X (inout vec4 Q, inout vec4 a, inout vec4 b, vec2 U, vec2 r) {
    vec4 Qn = C(U+r), qn = B(U+r), na = A(qn.xy), nb = A(Qn.xy);
   	float l = mp(U,a.xy,b.xy);

    if (mp(U,a.xy,na.xy)<l) {
    	Q = qn;
        b = na;
    }
    if (mp(U,a.xy,nb.xy)<l) {
        Q = Qn;
        b = nb;
    }
}
void Xr (inout vec4 Q, inout vec4 a, inout vec4 b, vec2 U, float r) {
	 X(Q,a,b,U,vec2(r,0));
     X(Q,a,b,U,vec2(0,r));
     X(Q,a,b,U,vec2(0,-r));
     X(Q,a,b,U,vec2(-r,0));
}
Main {
	Q = C(U);
    vec4 q = B(U),
         a = A(q.xy),
         b = A(Q.xy);
    Xr (Q,a,b,U,1.);
    Xr (Q,a,b,U,2.);
    Xr (Q,a,b,U,4.);
    Xr (Q,a,b,U,8.);

    Init Q = A(U);
}
