void mainImage( out vec4 o, in vec2 i )
{
 	vec2 ir = iResolution.xy;
    vec2 uv = i/ir;
    uv-=.5;
    uv.x*=ir.x/ir.y;

    uv*=8.-4.*cos(2.*iTime);
    if (length(uv)<8.88) uv*=.5+5.*cos(iDate.w)/length(uv);
    float col = 1.*length(round(.01*cos(iDate.w)+1.01*uv)-uv);
    o = vec4(col);
    o.rb-=.1*(uv);

   //if (length(uv)<1.5) o.rb+=.15*(uv);
    o+=.5*o*o;
}
