// inspired from https://shadertoy.com/view/tlcSW8

void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u/R;
    O = step( U.y/20. , T(vec2(U.x*640.,0)) / T(vec2(1)).w );
    O += O.b;
}

void mainImage( out vec4 O, vec2 U )
{
    O = T(U);
    if ( iMouse.z >= 0. ) {
        vec2 D = abs( iMouse.xy - T(vec2(1)).xy );
        float d = length( D );
        if ( d > 0. ) {
            if( U == vec2(1.5) )  O.w ++; 
            if ( int(U.x) == int(6.*d  ) ) O.b++;
            if ( int(U.x) == int(6.*D.x) ) O.r++;
            if ( int(U.x) == int(6.*D.y) ) O.g++;
        }
    }

    if(U==vec2(1.5))  O.xy = iMouse.xy;
}
